//------------------------------------------------------------------------------

using System;
using System.Reflection;
using System.Runtime.InteropServices;

[assembly: ComVisibleAttribute(false)]
[assembly: CLSCompliantAttribute(false)]
[assembly: AssemblyVersionAttribute("$assembly_version")]
[assembly: AssemblyFileVersionAttribute("$assembly_version")]
[assembly: AssemblyCopyrightAttribute("Â© Relativity")]
[assembly: AssemblyCompanyAttribute("Relativity ODA LLC")]
"@

$path_to_AssemblyInfo_cs = Join-Path $path_to_version_folder "AssemblyInfo.cs"
$cs_assemblyInfo | Set-Content $path_to_AssemblyInfo_cs