[CmdletBinding()]
param(
	[Parameter()]
	[String]$Version,
	[Parameter()]
	[String]$InformationalVersion,
	[Parameter()]
	[String]$VersionFolderPath = (Split-Path -Parent $MyInvocation.MyCommand.Path)
)

$assemblyInfoVb = @"
' -----------------------------------------------------------------------------------------------------------------------------------------------
' <auto-generated>
'     This source file is auto-generated by the Update-RdcAssemblySharedInfo.ps1
' </auto-generated>
' -----------------------------------------------------------------------------------------------------------------------------------------------

Imports System.Reflection
Imports System.Resources

<assembly: AssemblyCopyright("Relativity")>
<assembly: AssemblyTrademark("Relativity")>
<assembly: AssemblyCulture("")>

' Note: do NOT modify these values! This is automatically updated by the build script.
<assembly: AssemblyVersion("$Version")>
<assembly: AssemblyFileVersion("$Version")>
<assembly: AssemblyConfiguration("Release")>

<assembly: AssemblyCompany("Relativity ODA LLC")>
<assembly: NeutralResourcesLanguage("en-US")>
<assembly: AssemblyInformationalVersion("$InformationalVersion")>
"@

$assemblyInfoVbFile = Join-Path $VersionFolderPath "RdcAssemblySharedInfo.vb"
$assemblyInfoVb | Set-Content $assemblyInfoVbFile